import React from 'react'
import PropTypes from 'prop-types';
import {Card, Image, Popover, Button, Avatar} from "antd";
import {HeartOutlined,RetweetOutlined,MessageOutlined} from "@ant-design/icons";
import {useSelector} from "react-redux";

const PostCard = ({post}) =>{
    const {user} = useSelector((state)=>state.user);
    const id = me?.id;  // Optional Chaining연산자 me.id 가 있으면 그 값이고 , 없으면 undefined로 바꿔줌
    return(
      <div>
          {/*배열안에 js를 넣어줄때는 key를 꼭 넣어줘야함*/}
          <Card
              cover={post.Images[0] && <PostImages images={post.images}/>}
              actions={[
                  <RetweetOutlined key="retweet"/>,
                  <HeartOutlined key="heart"/>,
                  <MessageOutlined key="commnet"/>,
                  <Popover key="more" content={(
                      <Button.Group>
                          {id && post.User.id === id ? (
                              <>
                                <Button>수정</Button>
                                <Button type="danger">삭제</Button>
                              </>
                          ) :  <Button>신고</Button>}
                      </Button.Group>
                  )}>
                      <EllipsisOutlined/>
                  </Popover>,
              ]}
          >
              <Card.Meta
                  avatar = {<Avatar>{post.User.ninkname[0]}</Avatar>}
                  title = {post.User.nickname}
                  description={post.content}
              />
          </Card>
          <CommentForm/>
          <Comments/>
      </div>
    );
};

PostCard.propTypes = {
    post : PropTypes.shape({
        id:PropTypes.number,
        User:PropTypes.object,
        content : PropTypes.string,
        createdAt: PropTypes.object,
        Comments : PropTypes.arrayOf(PropTypes.object),
        Images : PropTypes.arrayOf(PropTypes.object),
    }).isRequired,

}

export default PostCard;
