import React from 'react'
import {Card,Image,Popover} from "antd";
import {HeartOutlined,RetweetOutlined} from "@ant-design/icons";

const PostCard = ({post}) =>{
    return(
      <div>
          {/*배열안에 js를 넣어줄때는 key를 꼭 넣어줘야함*/}
          <Card
              cover={post.Images[0] && <PostImages images={post.images}/>}
              actions={[
                  <RetweetOutlined key="retweet"/>,
                  <HeartOutlined key="heart"/>,
                  <MessageOutlined key="commnet"/>,
                  <Popover key="more" content={(
                      <Button.Group>

                      </Button.Group>
                  )}>
                      <EllipsisOutlined/>
                  </Popover>,
              ]}
          >
              <Image/>
              <Content/>
              <Buttons></Buttons>
          </Card>
          <CommentForm/>
          <Comments/>
      </div>
    );
};

export default PostCard;
